mapper.insert:
insert into user (id, username, open_id
  )
values (#{id,jdbcType=BIGINT}, #{username,jdbcType=VARCHAR}, #{openId,jdbcType=LONGVARCHAR}
  )

mapper.update:
update user
set username = #{username,jdbcType=VARCHAR},
  open_id = #{openId,jdbcType=LONGVARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, username, open_id
from user
where id = #{id,jdbcType=BIGINT}
